import { Product, ProductList, ProductListConfig } from 'src/@types/product.type';
import api from './api';
import { ResponseSuccessType } from 'src/@types/utils.type';
import {AxiosResponse} from "axios";

export const URL = '/product/get-list';


export const productApi = {
  // Lấy danh sách sản phẩm
  getProductList: (queryConfig: ProductListConfig) => {
    return api.post<ProductList>('/products', queryConfig); // Không cần AxiosResponse, chỉ định kiểu ProductList
  },

  // Lấy chi tiết sản phẩm theo ID
  getProduct: (id: string) => {
    return api.get<ResponseSuccessType<Product>>(`/products/${id}`); // Cập nhật đường dẫn URL cho sản phẩm
  }
};
