import { useQuery } from '@tanstack/react-query';
import { productApi } from 'src/api/product.api';
import { ProductListConfig } from 'src/@types/product.type';
import useQueryConfig from "../../hooks/useQueryConfig.tsx";
import {categoryApi} from "../../api/category.api.ts";
import {useEffect} from "react";
import {Helmet} from "react-helmet-async";
import AsideFilter from "./components/AsideFilter";
import SortProduct from "./components/SortProduct";
import Product from "./components/Product";
import Pagination from "../../components/Pagination";

export default function ProductList() {
  const queryConfig = useQueryConfig();

  const { data: productsData, isLoading: productsLoading } = useQuery({
    queryKey: ['productList', queryConfig],
    queryFn: () => productApi.getProductList(queryConfig as ProductListConfig),
    keepPreviousData: true
  });

  const { data: categoriesData, isLoading: categoriesLoading } = useQuery({
    queryKey: ['categories'],
    queryFn: () => categoryApi.getAll(),
    enabled: !productsLoading
  });

  useEffect(() => {
    if (productsData) {
      window.scrollTo(0, 0); // Cuộn lên đầu trang khi productsData có thay đổi
    }
  }, [productsData]);

  if (productsLoading || categoriesLoading) {
    return <div>Loading...</div>; // Hoặc có thể sử dụng spinner/loading component
  }

  const products = productsData?.content.map((product) => ({
    id: product.id,
    name: product.nameProduce || product.name, // Lấy tên sản phẩm từ `nameProduce` nếu có
    description: product.description,
    price: product.price,
    quantity: product.quantity,
    soldQuantity: product.soldQuantity,
    viewedQuantity: product.viewedQuantity,
    images: product.images ? product.images.split(',') : [], // Chuyển từ string sang mảng
    image: product.image,
    category: product.category ? {
      _id: product.category._id,
      name: product.category.name
    } : undefined,
  }));

  return (
      <div className="bg-gray-100 py-3">
        <Helmet>
          <title>Shopee Clone | Ho Hoang Sang</title>
          <meta name="description" content="Đây là dự án cá nhân và thực hiện không vì mục đích thương mại" />
        </Helmet>

        <div className="container">
          {productsData && (
              <div className="grid grid-cols-12 gap-6">
                <div className="col-span-2">
                  <AsideFilter queryConfig={queryConfig} categories={categoriesData?.data.data || []} />
                </div>

                <div className="col-span-10">
                  <SortProduct pageSize={productsData?.pagination.page_size} queryConfig={queryConfig} />
                  <div className="mt-4 grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5">
                    {products.map((product) => (
                        <Product key={product.id} product={product} />
                    ))}
                  </div>
                  <Pagination queryConfig={queryConfig} pageSize={productsData?.pagination.page_size} />
                </div>
              </div>
          )}
        </div>
      </div>
  );
}
