import React, { InputHTMLAttributes, useState } from 'react';
import type { UseFormRegister, RegisterOptions } from 'react-hook-form';

interface InputProps extends InputHTMLAttributes<HTMLInputElement> {
  errorMessage?: string;
  register?: UseFormRegister<any>;
  rules?: RegisterOptions;
  classNameInput?: string;
  classNameError?: string;
  classNameOpenEye?: string;
}

export default function Input(props: InputProps) {
  const {
    errorMessage,
    register,
    rules,
    name,
    classNameInput = 'w-full rounded-sm border border-gray-300 p-3 shadow-sm outline-none focus:border-gray-500',
    classNameError = 'mt-1 min-h-[1.25rem] text-red-500',
    className,
    classNameOpenEye = 'absolute right-[6px] top-[8px] h-5 w-5 cursor-pointer',
    ...rest
  } = props;

  const [openEyes, setOpenEyes] = useState(false);

  const registerInput = register && name ? register(name, rules) : {};

  const toogleEyes = () => {
    setOpenEyes((prev) => !prev);
  };

  const handleType = () => {
    if (rest.type === 'password') {
      let resultType = '';
      openEyes ? (resultType = 'text') : (resultType = 'password');

      return resultType;
    }

    return rest.type;
  };

  return (
    <React.Fragment>
      <div className={`relative ${className}`}>
        <input className={classNameInput} {...registerInput} {...rest} type={handleType()} />

        <div className={classNameError}>{errorMessage}</div>

        {rest.type === 'password' &&
          (openEyes ? (
            <svg
              xmlns='http://www.w3.org/2000/svg'
              fill='none'
              viewBox='0 0 24 24'
              strokeWidth={1.5}
              stroke='currentColor'
              className={classNameOpenEye}
              onClick={toogleEyes}
            >
              <path
                strokeLinecap='round'
                strokeLinejoin='round'
                d='M2.036 12.322a1.012 1.012 0 010-.639C3.423 7.51 7.36 4.5 12 4.5c4.638 0 8.573 3.007 9.963 7.178.07.207.07.431 0 .639C20.577 16.49 16.64 19.5 12 19.5c-4.638 0-8.573-3.007-9.963-7.178z'
              />
              <path strokeLinecap='round' strokeLinejoin='round' d='M15 12a3 3 0 11-6 0 3 3 0 016 0z' />
            </svg>
          ) : (
            <svg
              xmlns='http://www.w3.org/2000/svg'
              fill='none'
              viewBox='0 0 24 24'
              strokeWidth={1.5}
              stroke='currentColor'
              className={classNameOpenEye}
              onClick={toogleEyes}
            >
              <path
                strokeLinecap='round'
                strokeLinejoin='round'
                d='M3.98 8.223A10.477 10.477 0 001.934 12C3.226 16.338 7.244 19.5 12 19.5c.993 0 1.953-.138 2.863-.395M6.228 6.228A10.45 10.45 0 0112 4.5c4.756 0 8.773 3.162 10.065 7.498a10.523 10.523 0 01-4.293 5.774M6.228 6.228L3 3m3.228 3.228l3.65 3.65m7.894 7.894L21 21m-3.228-3.228l-3.65-3.65m0 0a3 3 0 10-4.243-4.243m4.242 4.242L9.88 9.88'
              />
            </svg>
          ))}
      </div>
    </React.Fragment>
  );
}
